        &lt;img src="https://via.placeholder.com/40" alt="User" /&gt;
      &lt;/div&gt;
    &lt;/div&gt;
  &lt;/header&gt;
&lt;/template&gt;

&lt;script setup&gt;
// No script needed for basic functionality
&lt;/script&gt;

&lt;style scoped&gt;
.workspace-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 24px;
