  color: #666;
  font-size: 14px;
}

.item-footer {
  font-size: 12px;
  color: #999;
}
&lt;/style&gt;


</content>
</write_to_file>

Finally, I'll create the create modal component:

<write_to_file>
<path>views/workspace/components/CreateModal.vue</path>
<content>



&lt;template&gt;
  &lt;div class="modal-overlay" @click.self="$emit('close')"&gt;
    &lt;div class="modal-content"&gt;
      &lt;h2&gt;Create New Bot&lt;/h2&gt;
      &lt;form @submit.prevent="handleSubmit"&gt;
        &lt;div class="form-group"&gt;
          &lt;label&gt;Bot Name&lt;/label&gt;
          &lt;input v-model="botName" type="text" placeholder="My Awesome Bot" required /&gt;
        &lt;/div&gt;
        &lt;div class="form-group"&gt;
          &lt;label&gt;Description&lt;/label&gt;
          &lt;textarea v-model="description" placeholder="What does your bot do?"&gt;&lt;/textarea&gt;
        &lt;/div&gt;
        &lt;div class="form-actions"&gt;
          &lt;button type="button" class="cancel-btn" @click="$emit('close')"&gt;Cancel&lt;/button&gt;
          &lt;button type="submit" class="create-btn"&gt;Create&lt;/button&gt;
        &lt;/div&gt;
      &lt;/form&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/template&gt;

&lt;script setup&gt;
import { ref } from 'vue'

const botName = ref('')
const description = ref('')

const handleSubmit = () =&gt; {
  // In a real app, this would create the bot
  console.log('Creating bot:', botName.value, description.value)
  emit('close')
}

const emit = defineEmits(['close'])
